package Algoritmi;
public class Zamena_vrednosti {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 0;
		int b = 2;
		int r;
		r = a;
		System.out.println("Vrednost r je: " + r);
		a = b;
		b = r;

		System.out.print("Zamenjena mesta: " + a + b);

	}

}
